class SnakeGame{constructor(e,t,i,s){this.gameSpeed=i,this.boardPixelDimensions=[e.width,e.height],this.boardGridDimensions=boardDimensions;const n=[Math.round(this.boardGridDimensions[0]/2),Math.round(this.boardGridDimensions[1]/2)];this.snakeState=[n],this.direction=0,this.lastGridTime=performance.now(),this.addEventListeners(),this.render()}addEventListeners(){window.addEventListener("keypress",(e=>{switch(e.which||e.keyCode){case 37:this.direction=1;break;case 38:this.direction=3;break;case 39:this.direction=2;break;case 39:this.direction=4}}))}generateApple(){}eatApple(){}isTouching(){return new Set(this.snakeState.map((e=>e[0]+","+e[1]))).size<this.snakeStat.length}calculateMovement(e){return Math.round(this.gameSpeed*(e-this.lastGridTime)/1e3)}repaint(e){}render(e){const t=this.calculateMovement(e);t>0&&(this.isTouching()||this.repaint(t)),setTimeout((()=>{window.requestAnimationFrame((e=>{this.render(e)}))}),16)}}new SnakeGame(document.getElementById("gameBoard"),document.getElementById("score"),20,5);